	  sBUTTONLIST : 'TFFTFTT'									//공통기능정의(조회,추가,삭제,저장,인쇄,엑셀,종료)
	, bFireEvent : true 											//이벤트 실행
	, bFdtlTM_CODEXD_oncelldatachange : true

};


/*-----------------------------------+
|  02.최초 화면 Load時 처리 할 사항   |
	@parameter : 
	@return :
+------------------------------------*/
scwin.initScreen = function() {

	//초기화 및 권한체크
	comLib.FormLoadSetting(sFORMCAPTION, sFORMLOCATION, scwin.globalVal.sBUTTONLIST, sUSERAUTH);

	//권한(1레벨은 모두 입력/수정/삭제가능, 그 외는 코드구분이 '업무'인 코드의 세부코드만 관리 가능함)        	
	if(GBL_AUTH_LEVEL == "1") {
		imgAddRow1.show();
		imgDelRow1.show();
	} else {
		imgAddRow1.hide();
		imgDelRow1.hide();
	}

	//공통코드
	var codeOption = [
		  {COMM_CODE : "SYST_CODE", object : [cmbSHRSYST_CODE], dataList : "SHRSYST_CODE"}
		, {COMM_CODE : "SYST_CODE", object : [cmbSYST_CODE, grdTM_CODEXH]}
		, {COMM_CODE : "CDGB_CODE", object : [cmbCDGB_CODE]}
	];
	comLib.setCodeObject(codeOption);

	//공통코드 filter
	dtlCommonCode_SYST_CODE.setCondition("filter", dtlCommonCode_SYST_CODE.getOption( "filterCondition" ) + " && REF3_FILD != '0' ");
	dtlCommonCode_SHRSYST_CODE.setCondition("filter", dtlCommonCode_SHRSYST_CODE.getOption( "filterCondition" ) + " && REF3_FILD != '0' ");


   	//그룹초기화
   	comLib.ObjReadOnly(grpTM_CODEXH,true, "");
   	comLib.ObjReadOnly(grpTM_CODEXD,true, "");

   	//초기 포커스
   	iptSHRCOMM_CDNM.focus();

};

