/*-----------------------------------+
|  08.서브미션 호출 시 넘어 갈 Argument 생성 및 서브미션 호출  |		
	@parameter : sMethodName(Task함수명)
	@return :
+------------------------------------*/ 
scwin.setSubmitArg = function (sMethodName) {

	var option;

	switch (sMethodName) {

		case 'SEARCH00' :

			option = {
				  id : sMethodName
				, action : "/TMMA0010/SEARCH00.do"
				, ref : "data:json, {'id':'dtmSEARCH00', 'key':'param'}"
				, target : "data:json, {'id':'dtlTM_CODEXH', 'key':'data'}"
				, submitDoneHandler : "setPostProcess" 
			};

			break;

		case 'SEARCH01' :
			//조회 Argument 생성
			dtmSEARCH01.set("COMM_CODE",dtlTM_CODEXH.getCellData(dtlTM_CODEXH.getRowPosition(), "COMM_CODE"));

			option = {
				  id : sMethodName
				, action : "/TMMA0010/SEARCH01.do"
				, target : "data:json, {'id':'dtlTM_CODEXD', 'key':'data'}"
				, ref : "data:json, {'id':'dtmSEARCH01', 'key':'param'}"
				, submitDoneHandler : "setPostProcess" 
			};

			break;

		case 'SAVE00' :

			option = {
				  id : sMethodName
			 	, action : "/TMMA0010/SAVE00.do"
				, ref : "data:json,[ {'id':'dtlTM_CODEXH', 'action':'modified', 'key':'TM_CODEXH'}"
								 +", {'id':'dtlTM_CODEXD', 'action':'modified', 'key':'TM_CODEXD'}]"
				, submitDoneHandler : "setPostProcess"
				, isShowMeg : true 
			};

			break;

		default :
       		break;       		
	}

	ajaxLib.executeSubmission(option);	 //서브미션 실행!

};

/*-----------------------------------+
|  09.서브미션 Transaction 후 Post Process  |		
	@parameter : sMethodName(Task함수명), e(서브미션 호출 후 리턴 Doc)
	@return :
+------------------------------------*/
scwin.setPostProcess = function (sMethodName, e) {

	if (ajaxLib.IsWarning(e)) {
		return;
	}

	switch (sMethodName) {

		case 'SEARCH00' :

			if (comLib.Result(e) > 0) {

				grdTM_CODEXH.setFocusedCell(0, 0);

			} else {

				comLib.ObjReadOnly(grpTM_CODEXH,true, "");
				scwin.setSubmitArg("SEARCH01");

			}

			comLib.Information(optInformation, e, "S");
			break;

		case 'SEARCH01' :
			if (dtlTM_CODEXD.getRowCount() > 0) {

				grdTM_CODEXD.setFocusedCell(0, 0);

			} else {

				comLib.ObjReadOnly(grpTM_CODEXD,true, "");

			}
			comLib.Information(optInformation1, e, "S");
			break;

		case 'SAVE00' :
			if (comLib.Result(e) > 0) {

				dtlTM_CODEXH.reform();
				dtlTM_CODEXD.reform();


      			var row = dtlTM_CODEXH.getRowPosition();
		       	scwin.globalVal.bFdtlTM_CODEXH_onrowpositionchange = false;	//행추가시 onrowpositionchange 이벤트 발생 방지
		       	grdTM_CODEXH.removeFocusedCell();
		       	scwin.globalVal.bFdtlTM_CODEXH_onrowpositionchange = true;	//행추가시 onrowpositionchange 이벤트 발생 방지
				grdTM_CODEXH.setFocusedCell( row , 0);

				comLib.Information(optInformation, e, "C");
			}
			break;

		default:
			break;

	}

};

