/*-----------------------------------+
|  12.그리드 row 변경 이벤트		     |		
	@parameter : info
	@return :
+------------------------------------*/	
scwin.changeRowIndex = function(row,oldRow, rtnValue) {

	if(!scwin.globalVal.bFireEvent) return;


	//공통코드상세 그리드에 변경된 데이터가 있다면 확인 후 처리!
	if(rtnValue == false) return;

	if(valLib.checkModified([dtlTM_CODEXD]) && rtnValue != true) {
		comLib.Message("TMM023", true, "changeRowIndex", row, oldRow);
		scwin.globalVal.bFireEvent = false;
		grdTM_CODEXH.setFocusedCell( oldRow, grdTM_CODEXH.getFocusedColumnIndex(), false );
		scwin.globalVal.bFireEvent = true;

		return false;
	} else if (rtnValue == true) {
		scwin.globalVal.bFireEvent = false;
		grdTM_CODEXH.setFocusedCell( row, 0, false );
		scwin.globalVal.bFireEvent = true;
	}

	//콤보 필터
	comLib.FilterUse(dtlTM_CODEXH, row, "SYST_CODE,CDGB_CODE");
	dtlCommonCode_SYST_CODE.setCondition("filter", dtlCommonCode_SYST_CODE.getOption( "filterCondition" ) + " && REF3_FILD != '0' ");

	if(dtlTM_CODEXH.getRowStatus(row) == "C") {

		comLib.ObjReadOnly(grpTM_CODEXH, false, "");

	} else {

		//권한 1레벨만 마스터 변경.
		//그외 권한레벨은 마스터 변경 불가. 상세코드 추가,삭제,수정불가!
		if(GBL_AUTH_LEVEL == "1") {

			comLib.ObjReadOnly(grpTM_CODEXH, false, "iptCOMM_CODE");
			iptCOMM_CODE.setReadOnly( true );

		} else {

			comLib.ObjReadOnly(grpTM_CODEXH, true, "");

		}

	}

	scwin.setSubmitArg("SEARCH01");
};

/*-----------------------------------+
|  13.데이터리스트 onChange 처리   |		
	@parameter : info.rowIndex, info.colID, info.oldValue, info.newValue
	@return :bool
+------------------------------------*/
scwin.changeDatalist = function(info) {

	if(info.colID == "COMM_CODE") {

		dtlTM_CODEXH.setCellData( info.rowIndex , info.colID ,info.newValue.toUpperCase() );

		//공통코드 상세의 공통코드 정보 업데이트
		for(var i=0; i < dtlTM_CODEXD.getTotalRow(); i++) {
			dtlTM_CODEXD.setCellData( i , "COMM_CODE" , info.newValue.toUpperCase() );
		}

	} 
};

