/*-----------------------------------+
|  16.삭제 여부 체크   |		
	@parameter : 
	@return :bool
+------------------------------------*/
scwin.checkDelete = function (objDtl) {	

	//삭제할 행
	var row = objDtl.getRowPosition();

	if(row < 0) return;

	switch (objDtl) {
		case dtlTM_CODEXH :
			// 한 건 삭제 처리시 해당 항목에 대한 삭제 여부 확인
			var sQuestionText = "공통코드명 : " + dtlTM_CODEXH.getCellData(row, "COMM_CDNM") + "("
							  + dtlTM_CODEXH.getCellData(row, "COMM_CODE") + ")";

			comLib.Message("TMM002", true, sQuestionText, "imgDelRow1_onclick", "", "1");
			break;

		case dtlTM_CODEXD :
			// 한 건 삭제 처리시 해당 항목에 대한 삭제 여부 확인
			var sQuestionText = "세부코드명 : " + dtlTM_CODEXD.getCellData(row, "COMD_CDNM") + "[ "
									 + dtlTM_CODEXD.getCellData(row, "COMD_CODE") + " ]";

			comLib.Message("TMM002", true, sQuestionText,"imgDelRow2_onclick", ""); 
			break;

		default :
			break;

	}

};

/*-----------------------------------+
|  17.저장 여부 체크   |		
	@parameter : 
	@return :bool
+------------------------------------*/	
scwin.checkSaveItem = function () {

	//변경된 자료가 있는지 체크
	if(valLib.checkModified([dtlTM_CODEXH,dtlTM_CODEXD], true)) {

		//필수 입력 항목 체크
		var valInfo00 = [ 
			{id : "COMM_CODE", component : "iptCOMM_CODE", mandatory : true, chklength : 9, isHangul : false}, //공통코드
			{id : "SYST_CODE", component : "cmbSYST_CODE", mandatory : true},	//시스템코드
			{id : "COMM_CDNM", component : "iptCOMM_CDNM", mandatory : true},	//공통코드명
			{id : "CDGB_CODE", component : "cmbCDGB_CODE", mandatory : true} 
	  	];	//코드구분

		var valInfo01 = [ 
			{id : "COMD_CODE", component : "iptCOMD_CODE", mandatory : true, isHangul : false}, //세부코드
			{id : "COMD_CDNM", component : "iptCOMD_CDNM", mandatory : true} 
		];	//세부코드명

	 	return valLib.validateGridView(grdTM_CODEXH, valInfo00) && valLib.validateGridView(grdTM_CODEXD, valInfo01);


 	} else {
 		return false;
 	}

};

