/***************************************************************************************************
*                                   컴포넌트 동적 Event 처리 부분                       		   *
***************************************************************************************************/
/*-----------------------------------+
|  상세 그리드 row 변경 이벤트   |
	@parameter : row, oldRow
	@return :
+------------------------------------*/
scwin.grdTM_CODEXD_onrowindexchange = function(row,oldRow) {

	if(dtlTM_CODEXD.getRowStatus(row) == "C") {

		comLib.ObjReadOnly(grpTM_CODEXD, false, "");

	} else {

		comLib.ObjReadOnly(grpTM_CODEXD, false, "iptCOMD_CODE");
		iptCOMD_CODE.setReadOnly( true );

		//권한레벨이 1 이하이고 공통코드의 코드구분이 시스템[0] 이면 세부코드정보를 수정할 수 없다!
		if(GBL_AUTH_LEVEL != "1" && dtlTM_CODEXH.getCellData(dtlTM_CODEXH.getRowPosition(), "CDGB_CODE") == "0") {

			comLib.ObjReadOnly(grpTM_CODEXD, true, "");
			imgAddRow2.setDisabled( true );
			imgDelRow2.setDisabled( true );

		} else {

			imgAddRow2.setDisabled( false );
			imgDelRow2.setDisabled( false );

		}

	}

};

/*-----------------------------------+
|  그리드 onChange 처리   |		
	@parameter : info.rowIndex, info.colID, info.oldValue, info.newValue
	@return :bool
+------------------------------------*/
scwin.dtlTM_CODEXD_oncelldatachange = function(info) {
	if (!scwin.globalVal.bFdtlTM_CODEXD_oncelldatachange) return;

	if(info.colID == "COMD_CODE") {

		var limit = WebSquare.util.parseInt(dtlTM_CODEXH.getCellData(dtlTM_CODEXH.getRowPosition(), "COCD_LNTH"), 0);
		//입력한 세부코드 길이가 마스터의 세부코드길이를 초과할 경우 오류!
		if(limit > 0) {
			if(comLib.Trim(info.newValue).length != limit) {
				comLib.Message("TMM005", true, "세부코드길이", limit);
				scwin.globalVal.bFdtlTM_CODEXD_oncelldatachange = false;
				dtlTM_CODEXD.setCellData(info.rowIndex , info.colID , info.newValue.substring(0,limit) );
				scwin.globalVal.bFdtlTM_CODEXD_oncelldatachange = true;
				iptCOMD_CODE.focus();
			}
		}

	} 
};

/*-----------------------------------+
|  공통코드마스터 행 추가 버튼 이벤트   |
	@parameter :
	@return :
+------------------------------------*/
scwin.imgAddRow1_onclick = function (e, rtnValue) {

	if (rtnValue == false) return;

	//공통코드상세 그리드에 변경된 데이터가 있다면 확인 후 처리!
	if(valLib.checkModified([dtlTM_CODEXD]) && rtnValue != true) {
		comLib.Message("TMM023", true, "imgAddRow1_onclick", e);
		return;
	}

	//행 추가
	var row = dtlTM_CODEXH.insertRow();

	// 초기값 세팅
	dtlTM_CODEXH.setCellData(row, "COCD_LNTH", "10");

	//추가한 행 선택
	grdTM_CODEXH.setFocusedCell(row, 0);

	//입력폼의 첫번째 컨트롤 선택ㅇ
	iptCOMM_CODE.focus();

};

/*-----------------------------------+
|  공통코드마스터 행 삭제 버튼 이벤트   |
	@parameter :
	@return :
+------------------------------------*/
scwin.imgDelRow1_onclick = function (e, callOrd, rtnValue) {

	if(rtnValue == false) return;

	if (dtlTM_CODEXH.getTotalRow() > 0) {

		if(rtnValue == true) {
			//세부코드 삭제 확인
			if (dtlTM_CODEXD.getRowCount() > 0 && callOrd == "1") {
				comLib.Message("TMM012", true, "imgDelRow1_onclick", e, "2");		
			} else {
				//데이터 삭제
		       	comLib.removeGridView(grdTM_CODEXH);

		       	//삭제후 마스터 focus
		       	var row = dtlTM_CODEXH.getRowPosition();
		       	scwin.globalVal.bFireEvent = false;	//행추가시 onrowpositionchange 이벤트 발생 방지
		       	grdTM_CODEXH.removeFocusedCell();
		       	scwin.globalVal.bFireEvent = true;	//행추가시 onrowpositionchange 이벤트 발생 방지
				grdTM_CODEXH.setFocusedCell( row , 0);

		       	if (dtlTM_CODEXH.getTotalRow() == 0) {	//삭제한 행이 마지막 행이라면..

		       		comLib.ObjReadOnly(grpTM_CODEXH,true, "");

		       	}
			}

        } else {
        	//삭제여부 확인
			scwin.checkDelete(dtlTM_CODEXH);

		}
	} else { 
		comLib.Message("TMM007",true);	//삭제 자료 없음 정보 Display
	}

};



/*-----------------------------------+
|  공통코드상세 행 추가 버튼 이벤트  |
	@parameter :
	@return :
+------------------------------------*/
scwin.imgAddRow2_onclick = function(e) {

	//공통코드 마스터가 없으면.. 입력 X
	if(comLib.Trim(dtlTM_CODEXH.getCellData(dtlTM_CODEXH.getRowPosition(), "COMM_CODE")) == "") {
		comLib.alert("공통코드마스터가 없습니다. 마스터코드를 먼저 입력 후 작업하시기 바랍니다!");
		return;
	}

	//행 추가
	var row = dtlTM_CODEXD.insertRow();

	// 초기값 세팅
	dtlTM_CODEXD.setCellData(row, "COMM_CODE", comLib.Trim(dtlTM_CODEXH.getCellData(dtlTM_CODEXH.getRowPosition(), "COMM_CODE")));
	dtlTM_CODEXD.setCellData(row, "CRTE_DATE", dateLib.CurrentDate("yyyyMMdd"));
	dtlTM_CODEXD.setCellData(row, "SORT_ORDR", grdTM_CODEXD.getRowCount() + "0");

	grdTM_CODEXD.setFocusedCell(row, "COMD_CODE");

	iptCOMD_CODE.focus();

};

/*-----------------------------------+
|  공통코드상세 행 삭제 버튼 이벤트   |
	@parameter :
	@return :
+------------------------------------*/
scwin.imgDelRow2_onclick = function (e, rtnValue) {

	if(rtnValue == false) return;

	if (dtlTM_CODEXD.getTotalRow() > 0) {

		if(rtnValue == true) {
			comLib.removeGridView(grdTM_CODEXD);

		//$w.log(dtlTM_CODEXD.getRowCount());
	      	if (dtlTM_CODEXD.getTotalRow() == 0) {	//삭제한 행이 마지막 행이라면..
				comLib.ObjReadOnly(grpTM_CODEXD,true, "");
	      	}
		} else {
			//삭제여부 확인
			scwin.checkDelete(dtlTM_CODEXD);

		}
	} else { 
		comLib.Message("TMM007",true);	//삭제 자료 없음 정보 Display
	}

};

