* @returns {Object} 
* @author kch
* @example
* @since
*/ 
scwin.imgAddRow1_onmouseover = function(e) {

	this.setStyle("cursor", "pointer");
	this.setSrc("/images/button/addRow_over.gif");

};

/**
* 행추가 버튼 mouseout event
* @date 2015. 04. 15
* @memberOf 
* @param {Object} argument
* @returns {Object} 
* @author kch
* @example
* @since
*/ 
scwin.imgAddRow1_onmouseout = function(e) {

	this.setStyle("cursor", "default");
	this.setSrc("/images/button/addRow.gif");

};

